var count = validateCount('${userInputForcount}');

/**
 * Validates Tweet Count 
 */
function validateCount(input) {
	if (!isNan(input) && input <= 100) {
	   return input;
	}
	throw 'Number of tweets per page can not exceed 100';
}

var pollingInterval = validatePollingInterval('${userInputForpollingInterval}');

/**
 * Validates Polling Interval 
 */
function validatePollingInterval(input) {
	if (!isNan(input)) {
	   return input;
	}
	throw 'Polling Interval should be a number';
}

