@App:name('TwitterAnalytics') 
@App:description('Twitter Analytics using Twitter Extension') 

@source(type='twitter',  consumer.key='${consumerKey}', consumer.secret='${consumerSecret}', 
access.token ='${accessToken}', access.token.secret='${accessTokenSecret}', mode="streaming",  track='${track}',  language = '${language}',  @map(type='keyvalue', @attributes(created_at = 'createdAt', id = 'tweetId' , text = 'text',location = 'user.location', hashtags = 'hashtags' , mentions = 'userMentions',trackwords = 'track.words')))

define stream Tweetstream(created_at string, id long, text string, location string, hashtags string, mentions string,trackwords string);
--define stream Tweetstream(created_at string, id long, text string, location string, hashtags string, mentions string);

define stream Sentimentstream(count long,id long, timestamp string,CDate string, TweetID string, value int,country string, hashtags string, mentions string);
define stream SentimentTypestream(count long,id long ,timestamp string,CDate string, TweetID string, value int, type string, country string , hashtags string, mentions string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define aggregation TweetAggre
from SentimentTypestream
select count() as count, avg(value) as average
aggregate by  timestamp every sec ... year;

@Store(type="rdbms",datasource="Twitter_Analytics", field.length="mentions:8000")
define table sentiment(count long,id long, timestamp string,CDate string, TweetID string, value int, type string, country string, hashtags string, mentions string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define table textCloud(id long, timestamp long, words string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define table hashtagCloud(id long, timestamp long, hashtag string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define table mentionCloud(id long, timestamp long, mention string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define table hashTag(id long, trackwords string);

--Sentiment Analysis
from Tweetstream
select count() as count,currentTimeMillis() as id,time:utcTimestamp() as timestamp,created_at as CDate,convert(id,'string') as TweetID, sentiment:getRate(text) as value,ifThenElse(location is null,'undefined',find:country(location))  as country, hashtags, mentions
insert into Sentimentstream;

from Sentimentstream[value<0]
select count, id,timestamp,CDate,TweetID, value,'negative' as type,country, hashtags, mentions
insert into SentimentTypestream;

from Sentimentstream[value>=0]
select count, id,timestamp,CDate,TweetID, value, ifThenElse(value>0,'positive','neutral') as type,country, hashtags, mentions
insert into SentimentTypestream;

@info(name='query1')
from SentimentTypestream
select *
insert into sentiment;

from Tweetstream
select currentTimeMillis() as id, trackwords
insert into Outputstream;

from Outputstream
select *
insert into hashTag;

--Words for text cloud
@info(name='Filtering words for text cloud')
from Tweetstream#text:tokenize(text)
insert all events into textStream;

from textStream[filter:word(token)]
select count() as id, currentTimeMillis() as timestamp,token as words
insert into Tokens;

@info(name='inserting into database')
from Tokens
select *
insert into textCloud;

--Words for hashtag cloud
@info(name='Filtering words for hashtag cloud')
from Tweetstream#entity:tokenize(hashtags)
select count() as id, currentTimeMillis() as timestamp, token as hashtag
insert all events into hashtagstream;

@info(name='inserting into hashtag cloud table')
from hashtagstream
select *
insert into hashtagCloud;

--Words for mention cloud
@info(name='Filtering words for mention cloud')
from Tweetstream#entity:tokenize(mentions)
select count() as id, currentTimeMillis() as timestamp, token as mention
insert all events into mentionstream;

@info(name='inserting into mention table')
from mentionstream
select *
insert into mentionCloud;

@App:name("PopularTweets")

@App:description('Retrieving Popular Tweets')

@source(type='twitter' , consumer.key='${consumerKey}', consumer.secret='${consumerSecret}', 
access.token ='${accessToken}', access.token.secret='${accessTokenSecret}', mode='polling', count = '${count}', query = '${query}', polling.interval = '${pollingInterval}', result.type = 'popular', @map(type='keyvalue', @attributes(createdAt = 'createdAt', id = 'tweetId', text= 'text')))

define stream TweetStream(createdAt string, id long, text string);

@Store(type="rdbms",datasource="Twitter_Analytics")
define table PopularTweet (timestamp long, TweetID string);

from TweetStream
select currentTimeMillis() as timestamp,convert(id,'string') as TweetID
insert into PopularStream;

from PopularStream
select *
insert into PopularTweet;
